import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const Roadmap = () => {
  const { skill } = useParams();
  const [roadmap, setRoadmap] = useState(null);

  useEffect(() => {
    const fetchRoadmap = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/roadmaps/${skill}`);
        const data = await response.json();
        setRoadmap(data);
      } catch (error) {
        console.error('Failed to fetch roadmap:', error);
      }
    };
    fetchRoadmap();
  }, [skill]);

  if (!roadmap) return <div>Loading...</div>;

  return (
    <div className="container mt-5">
      <h1>{roadmap.skill} Roadmap</h1>
      {roadmap.weeks.map((week) => (
        <div key={week.weekNum}>
          <h3>Week {week.weekNum}</h3>
          <ul>
            {week.topics.map((topic) => (
              <li key={topic.id}>{topic.name} ({topic.status})</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default Roadmap;
