import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Roadmap = ({ user }) => {
  const { skill } = useParams();
  const [roadmap, setRoadmap] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/roadmaps/${skill}`)
      .then((res) => res.json())
      .then((data) => setRoadmap(data))
      .catch((err) => alert('Error: ' + err.message));
  }, [skill]);

  if (!roadmap) return <div>Loading...</div>;

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">{skill} Roadmap</h1>
      {roadmap.weeks.map((week) => (
        <div key={week.weekNum} className="mb-4">
          <h2 className="text-xl font-semibold">Week {week.weekNum}</h2>
          {week.topics.map((topic) => (
            <div key={topic.id} className="border p-2 my-2">
              <p>{topic.name}</p>
              <p>Status: {topic.status || 'In Progress'}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Roadmap;
